<?php

namespace Fulbis\Core\Entity;

interface VersionableInterface {

    /**
     * Set autogenerated ID
     *
     * @param $id
     * @return mixed
     */
    public function setIdAuto($id);

    public function getIdAuto();

    /**
     * Get UUID
     *
     * @return mixed
     */
    public function getId();

    /**
     * Set deleted value
     *
     * @param $deleted
     * @return mixed
     */
    public function setDeleted($deleted);

}